import React, { useEffect, useState } from 'react';
import { Task } from './types/Task';
import { getTasks, addTask, deleteTask, updateTask } from './utils/api';
// import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
// import FilterBar from './components/FilterBar';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filter, setFilter] = useState({ from: '', to: '' });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await getTasks();
    setTasks(res.data);
  };

  const handleAdd = async (title: string) => {
    const newTask = { title, completed: false, createdAt: new Date().toISOString() };
    await addTask(newTask);
    fetchTasks();
  };

  const handleDelete = async (id: number) => {
    await deleteTask(id);
    fetchTasks();
  };

  const handleComplete = async (id: number) => {
    await updateTask(id, { completed: true });
    fetchTasks();
  };

  const filteredTasks = tasks.filter(task => {
    const createdDate = new Date(task.createdAt);
    const fromDate = filter.from ? new Date(filter.from) : null;
    const toDate = filter.to ? new Date(filter.to) : null;

    return (!fromDate || createdDate >= fromDate) &&
           (!toDate || createdDate <= toDate);
  });

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <AddTaskForm onAdd={handleAdd} />
      {/* <FilterBar filter={filter} setFilter={setFilter} />
      <TaskList tasks={filteredTasks} onDelete={handleDelete} onComplete={handleComplete} /> */}
    </div>
  );
};

export default App;
