import axios from 'axios';
import { Task } from '../types/Task';

const API_URL = 'http://localhost:3001/tasks';

export const getTasks = () => axios.get(API_URL);
export const addTask = (task: Omit<Task, 'id'>) => {
    console.log("POST to:", API_URL, "Payload:", task);
    return axios.post(API_URL, task);
  };
  export const deleteTask = (id: number) => axios.delete(`${API_URL}/${id}`);
export const updateTask = (id: number, data: Partial<Task>) => axios.patch(`${API_URL}/${id}`, data);
